//VERSION=3
function setup() {
    return {
        input: ["B03", "B04", "B08", "B11", "SCL"], // Requesting Green (B03) and SWIR (B11) bands
        output: {
            bands: 5,         // Output 4 bands (sample.B03, sample.B04, sample.B08, sample.B11)
            sampleType: "FLOAT32" // Use FLOAT32 for raw reflectance values
        }
    };
}

function evaluatePixel(sample) {
    // Return raw B03 and B08 values
    return [sample.B03, sample.B04, sample.B08, sample.B11, sample.SCL];
}
